import React, { useRef, useEffect } from 'react';
import * as THREE from 'three';

const WebGLBackground = () => {
  const mountRef = useRef(null);
  const sceneRef = useRef(null);
  const rendererRef = useRef(null);
  const frameRef = useRef(null);

  useEffect(() => {
    if (!mountRef.current) return;

    const mount = mountRef.current;
    const width = window.innerWidth;
    const height = window.innerHeight;

    // Scene setup
    const scene = new THREE.Scene();
    scene.fog = new THREE.Fog(0xf0f8ff, 100, 300);

    const camera = new THREE.PerspectiveCamera(75, width / height, 0.1, 1000);
    camera.position.z = 50;

    const renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true });
    renderer.setSize(width, height);
    renderer.setClearColor(0xf0f8ff, 1);
    renderer.shadowMap.enabled = true;
    renderer.shadowMap.type = THREE.PCFSoftShadowMap;

    mount.appendChild(renderer.domElement);

    // Store refs
    sceneRef.current = scene;
    rendererRef.current = renderer;

    // Data nodes (representing data infrastructure)
    const nodeGeometry = new THREE.SphereGeometry(0.3, 16, 16);
    const nodes = [];
    const nodeCount = 80;

    for (let i = 0; i < nodeCount; i++) {
      const nodeMaterial = new THREE.MeshPhongMaterial({
        color: new THREE.Color().setHSL(0.55 + Math.random() * 0.2, 0.4, 0.8),
        transparent: true,
        opacity: 0.7,
        emissive: new THREE.Color().setHSL(0.55 + Math.random() * 0.2, 0.3, 0.2)
      });

      const node = new THREE.Mesh(nodeGeometry, nodeMaterial);
      node.position.set(
        (Math.random() - 0.5) * 120,
        (Math.random() - 0.5) * 80,
        (Math.random() - 0.5) * 60
      );

      node.userData = {
        originalPosition: node.position.clone(),
        phase: Math.random() * Math.PI * 2,
        speed: 0.5 + Math.random() * 1.5
      };

      nodes.push(node);
      scene.add(node);
    }

    // Connection lines (data flow)
    const connectionMaterial = new THREE.LineBasicMaterial({
      color: 0x00ffaa,
      transparent: true,
      opacity: 0.3
    });

    const connections = [];
    for (let i = 0; i < nodeCount / 4; i++) {
      const node1 = nodes[Math.floor(Math.random() * nodes.length)];
      const node2 = nodes[Math.floor(Math.random() * nodes.length)];

      if (node1 !== node2 && node1.position.distanceTo(node2.position) < 30) {
        const geometry = new THREE.BufferGeometry().setFromPoints([
          node1.position,
          node2.position
        ]);
        const line = new THREE.Line(geometry, connectionMaterial);
        connections.push({ line, node1, node2 });
        scene.add(line);
      }
    }

    // Floating geometric shapes (microservices)
    const geometries = [
      new THREE.OctahedronGeometry(1),
      new THREE.TetrahedronGeometry(1.2),
      new THREE.IcosahedronGeometry(0.8)
    ];

    const floatingShapes = [];
    for (let i = 0; i < 25; i++) {
      const geometry = geometries[Math.floor(Math.random() * geometries.length)];
      const material = new THREE.MeshPhongMaterial({
        color: new THREE.Color().setHSL(0.5 + Math.random() * 0.3, 0.7, 0.5),
        transparent: true,
        opacity: 0.6,
        wireframe: Math.random() > 0.5
      });

      const shape = new THREE.Mesh(geometry, material);
      shape.position.set(
        (Math.random() - 0.5) * 100,
        (Math.random() - 0.5) * 60,
        (Math.random() - 0.5) * 40
      );

      shape.userData = {
        rotationSpeed: new THREE.Vector3(
          (Math.random() - 0.5) * 0.02,
          (Math.random() - 0.5) * 0.02,
          (Math.random() - 0.5) * 0.02
        ),
        floatSpeed: 0.3 + Math.random() * 0.7,
        floatRange: 5 + Math.random() * 10
      };

      floatingShapes.push(shape);
      scene.add(shape);
    }

    // Particle system (data streams)
    const particleCount = 200;
    const particleGeometry = new THREE.BufferGeometry();
    const positions = new Float32Array(particleCount * 3);
    const velocities = new Float32Array(particleCount * 3);

    for (let i = 0; i < particleCount * 3; i += 3) {
      positions[i] = (Math.random() - 0.5) * 200;
      positions[i + 1] = (Math.random() - 0.5) * 100;
      positions[i + 2] = (Math.random() - 0.5) * 100;

      velocities[i] = (Math.random() - 0.5) * 0.5;
      velocities[i + 1] = (Math.random() - 0.5) * 0.3;
      velocities[i + 2] = (Math.random() - 0.5) * 0.5;
    }

    particleGeometry.setAttribute('position', new THREE.BufferAttribute(positions, 3));

    const particleMaterial = new THREE.PointsMaterial({
      color: 0x00aaff,
      size: 0.5,
      transparent: true,
      opacity: 0.7
    });

    const particles = new THREE.Points(particleGeometry, particleMaterial);
    scene.add(particles);

    // Lighting
    const ambientLight = new THREE.AmbientLight(0x404040, 0.3);
    scene.add(ambientLight);

    const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
    directionalLight.position.set(10, 10, 5);
    directionalLight.castShadow = true;
    scene.add(directionalLight);

    const pointLight = new THREE.PointLight(0x00ffaa, 0.5, 50);
    pointLight.position.set(-20, -20, 20);
    scene.add(pointLight);

    // Animation
    let time = 0;
    const animate = () => {
      frameRef.current = requestAnimationFrame(animate);
      time += 0.01;

      // Animate nodes
      nodes.forEach((node, index) => {
        const userData = node.userData;
        node.position.y = userData.originalPosition.y + Math.sin(time * userData.speed + userData.phase) * 3;
        node.rotation.x += 0.01;
        node.rotation.y += 0.015;

        // Pulse effect
        const scale = 1 + Math.sin(time * 2 + userData.phase) * 0.2;
        node.scale.setScalar(scale);
      });

      // Update connections
      connections.forEach(({ line, node1, node2 }) => {
        const geometry = line.geometry;
        const positions = geometry.attributes.position.array;
        positions[0] = node1.position.x;
        positions[1] = node1.position.y;
        positions[2] = node1.position.z;
        positions[3] = node2.position.x;
        positions[4] = node2.position.y;
        positions[5] = node2.position.z;
        geometry.attributes.position.needsUpdate = true;
      });

      // Animate floating shapes
      floatingShapes.forEach(shape => {
        const userData = shape.userData;
        shape.rotation.x += userData.rotationSpeed.x;
        shape.rotation.y += userData.rotationSpeed.y;
        shape.rotation.z += userData.rotationSpeed.z;

        shape.position.y += Math.sin(time * userData.floatSpeed) * 0.02;
      });

      // Animate particles
      const positions = particles.geometry.attributes.position.array;
      for (let i = 0; i < particleCount * 3; i += 3) {
        positions[i] += velocities[i];
        positions[i + 1] += velocities[i + 1];
        positions[i + 2] += velocities[i + 2];

        // Reset particles that go too far
        if (Math.abs(positions[i]) > 100 || Math.abs(positions[i + 1]) > 50 || Math.abs(positions[i + 2]) > 50) {
          positions[i] = (Math.random() - 0.5) * 200;
          positions[i + 1] = (Math.random() - 0.5) * 100;
          positions[i + 2] = (Math.random() - 0.5) * 100;
        }
      }
      particles.geometry.attributes.position.needsUpdate = true;

      // Camera movement
      camera.position.x = Math.sin(time * 0.1) * 10;
      camera.position.y = Math.cos(time * 0.15) * 5;
      camera.lookAt(0, 0, 0);

      renderer.render(scene, camera);
    };

    animate();

    // Handle window resize
    const handleResize = () => {
      const newWidth = window.innerWidth;
      const newHeight = window.innerHeight;

      camera.aspect = newWidth / newHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(newWidth, newHeight);
    };

    window.addEventListener('resize', handleResize);

    // Cleanup
    return () => {
      const mount = mountRef.current;
      window.removeEventListener('resize', handleResize);
      if (frameRef.current) {
        cancelAnimationFrame(frameRef.current);
      }
      if (mount && renderer.domElement) {
        mount.removeChild(renderer.domElement);
      }
      renderer.dispose();
    };
  }, []);

  return <div ref={mountRef} style={{ position: 'absolute', top: 0, left: 0, zIndex: -1 }} />;
};

export default WebGLBackground;
